  const [rotateDirection, setRotateDirection] = useState("clockwise");

  return (
    <View>
      <Row>
        <Button
          onPress={() => {
            setRotated(!rotated);
            setRotateDirection("clockwise");
          }}
        >
          clockwise
        </Button>
        <Spacer size={15} />
        <Button
          onPress={() => {
            setRotated(!rotated);
            setRotateDirection("counterclockwise");
          }}
        >
          counter-clockwise
        </Button>
      </Row>

      <Icon
        name="chevron down"
        rotated={rotated}
        rotateDirection={rotateDirection}
        size={30}
      />
    </View>
  );
};

export default IconExampleRotated;

